import Card from 'react-bootstrap/Card';
import { useState, useEffect } from 'react'
import getFetch from '../Data/productos'


export default function Item () {
    function descrItem () {
        return(
          alert('¡Sitio en construcción!')
        );
      }
    const [Data,setData]=useState([])
    const [loading,setLoading]=useState(true)

    useEffect(()=>{
    getFetch
    .then((resp)=>setData(resp))
    .catch(err=>console.log(err))
    .finally(()=>setLoading(false))
    }, [])


    return(
        <div>
            {
                loading ?  <h3>¡Aguarde!, sus productos estan siendo cargados</h3> : Data.map(prod =>
                    <div className='container'>
                        <Card style={{ width: '18rem' }}>
                        <Card.Img key={prod.imagen} variant="top" src={prod.imagen} />
                        <Card.Body>
                            <Card.Title key={prod.nombre}>{prod.nombre}</Card.Title>
                            <Card.Text key={prod.precio}>
                                {prod.precio}
                            </Card.Text>
                            <button onClick={descrItem} className='btnDescrItem'>¡Ver descripcion del producto!</button>
                        </Card.Body>
                        </Card> 
                    </div>
                    
                )
        }
        </div>
        
    )

}
