import{ useState} from 'react'
import './index.css'


export default function Counter (props) {
    const [Counter, setCounter] = useState(props.initial);
    
    function addCounter ( ){
        if (Counter >= props.stock){
                alert(`El stock maximo es ${props.stock}`);
        } else {
            setCounter (Counter + 1);
        }

    }

    function reduceCounter () {
        if (Counter <= 1) {
            alert("¡Usted no ha seleccionado ningun producto!")
        } else {
            setCounter (Counter - 1)
        }
    }

    return (
        <div className='container d-flex  justify-content-center col-8'>
            <div className='container d-flex align-items-center fw-bolder'>
                <button className='col-5 btnAdd' onClick={addCounter}>+</button>
                <span className='col-2 m-2 fs-4'> {Counter}</span>
                <button className='col-5 btnRedd' onClick={reduceCounter}>-</button>    
            </div>
        </div>
    )
}
