import Card from 'react-bootstrap/Card';
import Counter from '../ItemCount';
import { useState, useEffect } from 'react'
import getFetch from '../Data/productos'
import './index.css'


export default function Item () {
    function descrItem () {
        return(
          alert('¡Sitio en construcción!')
        );
      }
    const [Data,setData]=useState([])
    const [loading,setLoading]=useState(true)

    useEffect(()=>{
    getFetch
    .then((resp)=>setData(resp))
    .catch(err=>console.log(err))
    .finally(()=>setLoading(false))
    }, [])


    return(
        <div className='container d-flex  col-12 m-0 p-0 justify-content-evenly nowrap'>
            {
                loading ?  <h3 className='text-dark text-center bg-warning'>¡Aguarde!, sus productos estan siendo cargados</h3> : Data.map(prod =>
                    
                        <Card className='container flex row col-2 wrap' >
                        <Card.Img  variant="top" src={"../" + prod.imagen} className="img-fluid" />
                        <Card.Body>
                            <Card.Title  >{prod.nombre}</Card.Title>
                            <Card.Text >
                                    ${prod.precio}
                            </Card.Text>
                            <button onClick={descrItem} className='btnDescrItem mb-3'>¡Ver descripcion del producto!</button>
                            <Counter  stock={prod.stock} initial={1}/>
                        </Card.Body>
                        </Card> 
                )
        }
        </div>
        
    )

}
